<?php/*Plugin Name: VainCode Facebook Stick BoxPlugin URI: http://market.vaincode.com/product/facebook-stick-box/Description: This plugin will add a Stick Box on the Right Side of Website, which contain a facebook page, when an user move mouse pointer on the box the facebook like page will apper.Author: Md. Abdullah Al MahimAuthor URI: http://fb.com/a.a.mahinVersion: 1.2*//*-- Adding Latest jQuery from Wordpress --*/function aam_fb_stick_box_jquery() {	wp_enqueue_script('jquery');}add_action('init', 'aam_fb_stick_box_jquery');/*-- Some Set-up --*/define('AAM_FB_STICK_HOOK', WP_PLUGIN_URL . '/' . plugin_basename( dirname(__FILE__) ) . '/' );/*-- Callback FB Stick JS Under main Jquery Library --*/wp_enqueue_script('aam-fb-stick-box-ready-js', AAM_FB_STICK_HOOK.'js/aam-fb-stick-ready.js', array('jquery'));/*-- Enque Style's For aam fb stick box --*/wp_enqueue_style('aam-fb-stick-box-custome-style', AAM_FB_STICK_HOOK.'css/aam-fb-stick.css');/*-- Enque Style's For Admin Panel --*/function aam_fb_box_admin_plugin_styles() {    wp_enqueue_style( 'stylish-admin-panel-plugin-style', AAM_FB_STICK_HOOK.'css/admin_css.css' );}add_action( 'admin_head', 'aam_fb_box_admin_plugin_styles' );/*-- Add aam scroll menu into admin --*/function aam_fb_stick_setting_menu() {add_menu_page('VainCode Facebook Stick Box Settings', 'AAM FB Box', 'administrator', __FILE__, 'aam_fb_stick_setting_sec');add_action( 'admin_init', 'register_fb_stick_inputs' );}add_action('admin_menu', 'aam_fb_stick_setting_menu');/*-- Register Custome Options For Control Panel Admin --*/function register_fb_stick_inputs() {	//register settings	register_setting( 'aam-fb-box-settings-group', 'aam_fb_page' );}//Control Panel Admin Contentsfunction aam_fb_stick_setting_sec() {?><div id="wrap"><h2 id="page_title">VainCode Facebook Stick Box Setting Page</h2><form method="post" action="options.php" id="news_form">    <?php settings_fields( 'aam-fb-box-settings-group' ); ?>    <?php do_settings_sections( 'aam-fb-box-settings-group' ); ?>		<div>		<label for="input_box_source">Facebook Fan Page URL</label>		<input id="input_box_source" type="text" name="aam_fb_page" value="<?php if(get_option('aam_fb_page')) : echo get_option('aam_fb_page'); else : echo ""; endif; ?>" required/>		<span class="description"><p>Enter Your Facebook Fan Page Full URL Here. (https://www.facebook.com/facebook) </p></span>	</div>			<center>		 <?php submit_button(); ?>	</center></form></div><?php}/*--FB Stick box Content --*/function aam_fb_stick_content() {$aam_fb_page 		= get_option('aam_fb_page');if($aam_fb_page==""){$fb_page_url = "https://www.facebook.com/facebook";}else{$fb_page_url = $aam_fb_page;}?><div class="aam_fb_stick" style="">  <div>   <iframe src="http://www.facebook.com/plugins/likebox.php?href=<?php echo $fb_page_url; ?>&amp;width=245&amp;colorscheme=light&amp;show_faces=true&amp;border_color=white&amp;connections=12&amp;stream=false&amp;header=false&amp;height=270" scrolling="no" frameborder="0" scrolling="no" style="none; overflow: hidden; height: 290px; width: 245px;background:#fafafa;"></iframe><span>Plugin by:<a href="http://market.vaincode.com/product/facebook-stick-box/" target="_blank">aAM</a></span>  </div></div><?php}add_action( 'wp_footer', 'aam_fb_stick_content' );?>